import React, { Component } from "react";

export default class Score extends Component {
    state = {
        teamName:'',
        team: []
    }

    addTeam = () => {
        const newTeamList = [...this.state.team]
        newTodoList.push(this.state.teamName)
        this.setState({
          teamName:'',
          team: newTodoList
        })
      }
  render() {
    return (
      <main className="score-main">
        <h1>Pick your team</h1>
        <form
          className="unos"
        >
          <input
            type="text"
            onChange={(e) => {
              this.setState({teamName: e.target.value})
            }}
          />
          <button type="submit">Submit</button>
        </form>
      </main>
    );
  }
}
