import React , {useState, useEffect}from 'react'
import { Link, useParams } from 'react-router-dom';
const URL = "https://js-course-server.onrender.com/quotes/get-quote/";

function QuoteDetTest() {
  const [moreDetails, setMoreDetails] = useState([]);
  const params = useParams();
  const id=params.id;

  useEffect(() => {
    fetch(URL + id)
      .then((data) => data.json())
      .then((res) => setMoreDetails(res))
      .catch((err) => console.log(err));
  }, []);

  console.log(moreDetails)
    return (
    <div>
      <h1>{moreDetails.quoteAuthor} </h1>
      <h1>{moreDetails.quoteText} </h1>
      <h1>{moreDetails.likes} </h1>
      <Link></Link>
    </div>
  )
}

export default QuoteDetTest
