import React, { Component } from "react";
import "./scoreApp.css";

export default class ScoreApp extends Component {
state = {
    homeScore: 0,
    guestScore: 0
}

  render() {
    return (
      <div className="score-cont">
        <div className="score-card">
            <h1>{this.state.homeScore}</h1>
          <p className="home">Home</p>
          <button className="btn" onClick={() => this.state.homeScore = this.state.homeScore + 1}>Goal</button>
        </div>
        <div className="score-card">
            <h1>{this.state.guestScore}</h1>
          <p className="home">Guest</p>
          <button className="btn">Goal</button>
        </div>
      </div>
    );
  }
}
