import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Formik } from "formik";
import * as yup from "yup";

function EditQuote() {
  const state = useLocation();
  const [quoteData, setQuoteData] = useState(state.state);
  console.log(quoteData);
  const URL = "https://js-course-server.onrender.com/quotes/edit/";
  const token = localStorage.getItem("authToken");
  const navigate = useNavigate();

  const updateData = () => {

    navigate("/quote");
    fetch(`https://js-course-server.onrender.com/quotes/edit/${quoteData.category}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        Authorization: token,
      },
    })
      .then((data) => data.json())
      .then((res) => setQuoteData(res))
      .catch((err) => console.log(err));
  };
  return (
    <div>
      <input
        type="text"
        placeholder="change name"
        value={quoteData.quoteAuthor}
        onChange={(event) =>
          setQuoteData({ ...quoteData, quoteAuthor: event.target.value })
        }
      />
      <input
        type="text"
        placeholder="change text"
        value={quoteData.quoteText}
        onChange={(event) =>
          setQuoteData({ ...quoteData, quoteText: event.target.value })
        }
      />
      <input
        type="text"
        placeholder="change name"
        value={quoteData.quoteSource}
        onChange={(event) =>
          setQuoteData({ ...quoteData, quoteSource: event.target.value })
        }
      />
      <button onClick={updateData}>Submit</button>
    </div>
  );
}

export default EditQuote;
