  formData.append('json1', JSON.stringify(quotesData));

  fetch('http://localhost:3000//', {
    method: 'POST', 
    mode: 'cors', 
    body: formData

  })
}

  return (
    <div className='quote-main'>
        {quotesData.map((quote) => {
            return <div key={quote._id} className='quote-card'>
                <h1>{quote.quoteAuthor}</h1>
                <h3>{quote.quoteSource}</h3>
                <p>{quote.quoteText}</p>
            <div className="likes">
            <button className={`like-btn ${liked ? 'liked' : ''}`}
            onClick={handleLike}
            >33</button>
            <p>{quote.likes}</p>

            </div>
                <Link to={`/quote/${quote._id}`}>Open nesto</Link>

                 </div>
        })}
    </div>
  )
}

export default AllQuotes
