import React from "react";
import { Formik } from "formik";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";

const loginSchema = yup.object({
  email: yup.string().required().email(),
  password: yup.string().required().min(6).max(50),
  confirmPassword: yup.string().required().min(6).max(50),
  fullName: yup.string.required.min(2).matches(/^[a-zA-Z]{2}$/i)
});
function SignUp() {
    const navigate = useNavigate();

  return (
    <div className="signUp-wrapper">
<Formik
        initialValues={{ email: "", password: "", confirmPassword: "", fullName: ""}}
        onSubmit={(values, actions) => {
          fetch("https://js-course-server.onrender.com/user/login", {
            method: "POST",
            body: JSON.stringify(values),
            headers: {
              "Content-Type": "application/json",
            },
          })
            .then((res) => res.json())
            .then((data) => {
              if (data.token) {
                navigate("/");
              }
            });
        }}
        validationSchema={loginSchema}
      
      >
        {({
          values, // formikov state
          errors, // errors = { email: 'Neispravan email' }
          touched, // touched = { email: true }
          handleChange,
          handleBlur,
          handleSubmit,
        }) => (
          <div>
            <button
              onClick={() => {
                console.log(values, "values");
                console.log(errors, "errors");
                console.log(touched, "touched");
              }}
            >
              Console log states
            </button>
            <div className="input-wrapper">
              <input
                type="text"
                name="fullName"
                placeholder="Full name"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.fullName}
              />
              <p className="error-message">
                {errors.fullName && touched.fullName && errors.fullName}
              </p>
            </div>
            
            <div className="input-wrapper">
              <input
                type="email"
                name="email"
                placeholder="Email"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.email}
              />
              <p className="error-message">
                {errors.email && touched.email && errors.email}
              </p>
            </div>

            <div className="input-wrapper">
              <input
                type="password"
                name="password"
                placeholder="Enter password"

                onChange={handleChange}
                onBlur={handleBlur}
                value={values.password}
              />
              <p className="error-message">
                {errors.password && touched.password && errors.password}
              </p>
            </div>

            <div className="input-wrapper">
              <input
                type="password"
                name="password"
                placeholder="Confirm password"
                onChange={handleChange}
                onBlur={handleBlur}
                value={values.confirmPassword}
              />
              <p className="error-message">
                {errors.confirmPassword && touched.confirmPassword && errors.confirmPassword}
              </p>
            </div>

            <button onClick={handleSubmit} type="button">
              Submit
            </button>
          </div>
        )}
      </Formik>
    </div>
  )
}

export default SignUp
